function generatePassword() {
    var uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
	var lowercase = 'abcdefghijklmnopqrstuvwxyz';
	var numberchar = '0123456789';
	var symbolchar = '!@#$%^&*()_+~`|}{[]:;?><,./-=';
	var password_length = document.getElementById('password-length').value;
	var uppercaseCheck = document.getElementById('checkbox-uppercase').checked;
	var lowercaseCheck = document.getElementById('checkbox-lowercase').checked;
	var numberCheck = document.getElementById('checkbox-number').checked;
	var specialCheck = document.getElementById('checkbox-symbol').checked;

	// set password included string
	var password = '';
	// uppercase
	if (uppercaseCheck) {
		password += uppercase;
	}
	// lowercase
	if (lowercaseCheck) {
		password += lowercase;
	}
	// numberic
	if (numberCheck) {
		password += numberchar;
	}

	// symbol
	if (specialCheck) {
		password += symbolchar;
	}

	var returnpassword = '';

    for (var i = 0, n = password.length; i < password_length; ++i) {
        returnpassword += password.charAt(Math.floor(Math.random() * n));
    }
    return returnpassword;
}

// render password
$(document).ready(function() {
	$('#generated-password').val(generatePassword());
	
	// refresh password
	$('#password-generate-button').click(function(event) {
		event.preventDefault();
		$('#generated-password').val(generatePassword());
	});

	// when custom status change
	$('.custom-password').change(function(event) {
		event.preventDefault();
		$('#generated-password').val(generatePassword());
	});

	// when custom status change
	$('body').on('change', '#password-length', function(event) {
		event.preventDefault();
		$('#generated-password').val(generatePassword());
	});

	// copy text
    $('#copy-password').on('click', function() {
        $('#generated-password').select();
        document.execCommand("copy");
        iziToast.success({
            maxWidth: 400,
            timeout: 10000,
            position: 'topRight',
            title: 'Copy alert',
            message: 'Password copied.',
        });
    });
});
